    }

    void close() override {
        std::cout "Window is closing!" 
    }

    void textInputEvent(unsigned int C) override {

    }

    void keyPressedEvent(ffw::Key key, ffw::Mode mode) override {
        if(mode == ffw::Mode::PRESSED)
            std::cout "Key pressed: " 
    }

    void mouseMovedEvent(int mousex, int mousey) override {
        std::cout "Mouse moved to: " "x" 
    }

    void mouseScrollEvent(int scroll) override {
        std::cout "Mouse scroll: " 
    }

    void mouseButtonEvent(ffw::MouseButton button, ffw::Mode mode) override {
        if(mode == ffw::Mode::PRESSED){
            switch(button){
                case ffw::MouseButton::LEFT: std::cout "Left mouse button pressed!" break;
                case ffw::MouseButton::MIDDLE: std::cout "middle mouse button pressed!" break;
                case ffw::MouseButton::RIGHT: std::cout "Right mouse button pressed!" break;
                default: break;
            }
        }
    }

    void windowResizedEvent(int width, int height) override {
        std::cout "Window resized to: " "x" 
    }

    void windowMovedEvent(int windowx, int windowy) override {
        std::cout "Window moved to: " "x" 
    }

    void windowFocusEvent(bool focus) override {
        if(focus)std::cout "Focus gained!" 
        else std::cout "Focus lost!" 
    }

    void windowCloseEvent() override {
        std::cout "Window close button pressed!" 
        this->shouldClose(true);
    }

    void filesDroppedEvent(std::vector filelist) override {
        std::cout "Files dropped: " 
        for(const auto& file : filelist){
            std::cout "\t\'" "\'" 
        }
    }

};

int main(int argc, char *argv[]){
    // Instance to our app class
    App app;

    // set arguments
    ffw::GLFWRenderWindowArgs args;
    args.size.set(400, 400);
    args.title = "Empty Example";

    // create window
    if(!app.create(args, NULL)){
        std::cerr "Failed to create window!" 
        return 1;
    }

    // Run setup
    if(!app.setup()){
        std::cerr "Failed to setup window!" 
        return 1;
    }

    // The main window loop
    while(app.shouldRender()){
        app.renderFrame();
        app.poolEvents();
    }

    // destroy window, this will delete all graphics data used by the window.
    // Must be called after the setup and before the graphics
    // is terminated
    app.destroy();
    return 0;
}




Now compile it and run it! You might see the following message:

This is where DLL files comes...
For the Debug, simply copy all DLL and PDB files with '_d' suffix from C:\path\to\ffw-win32-vc12\bin into C:\Path\To\Your\Project Folder\Debug (this folder contains your executable file!)
For the Release, simply copy all DLL (with NO '_d' suffix!) from C:\path\to\ffw-win32-vc12\bin into C:\Path\To\Your\Project Folder\Release (this folder contains your executable file!)
